import React, { Component } from 'react';
import Formulario from '../components/formulario/Formulario';

import 'bootstrap/dist/css/bootstrap.min.css';




class PageCadastros extends Component {

    constructor() {
      super();
      this.state = {
        pessoas : []
      }
    }

    criarPessoa(email, senha, estado) {
      const novaPessoa =  {email, senha, estado};
      const novoArrayPessoas = [...this.state.pessoas, novaPessoa]
      const novoEstado = {
        pessoas:novoArrayPessoas
      }
      this.setState(novoEstado)
      console.log(novoArrayPessoas)
    }


  render() {
    return (

      <Formulario criarPessoa={this.criarPessoa.bind(this)}/>
    )
  }
}

export default PageCadastros;
