import React, { Component } from 'react';
import NavBar from "./components/navBar/NavBar.jsx"
import PageListagem from "./pages/PageListagem.jsx"
import Formulario from "./components/formulario/Formulario.jsx"
import 'bootstrap/dist/css/bootstrap.min.css';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from 'react-router-dom';


class App extends Component{

  constructor() {
    super();
    this.state = {
      pessoas: []
    }
  }

  criarPessoa(email, senha, estado) {
    const novaPessoa = { email, senha, estado };
    const novoArrayPessoas = [...this.state.pessoas, novaPessoa]
    const novoEstado = {
      pessoas: novoArrayPessoas
    }
    this.setState(novoEstado)
    console.log(this.state.pessoas)

  }

  render() {


  return (
    <>

      <NavBar />
      <Router>

        <h3 align="center">
          <Link to='PageListagem'>PESSOAS CADASTRADAS</Link>
          <br></br>
          <Link to='formulario'>FORMUL√ÅRIO</Link>
        </h3>
        <Routes>

          <Route path='/formulario' element={<Formulario criarPessoa={this.criarPessoa.bind(this)}/>} />

        </Routes>
        <PageListagem pessoas={this.state.pessoas}/>


      </Router>

    </>

  );
  }
}

export default App;
