import React, { Component } from 'react';
import "./style.css"
import TituloFormulario from "./TituloFormulario"

class Formulario extends Component {

    constructor(props) {
        super(props);
        this.email= "";
        this.senha= "";
        this.estado= "";
      }
  
    _criarPessoa(evento) {
        evento.preventDefault();
        evento.preventPropagation();
        this.props.criarPessoa(this.email, this.senha, this.estado);
    }

    _handleMudancaEmail(evento) {
        evento.stopPropagation();

        this.email = evento.target.value;
    }

    _handleMudancaSenha(evento) {
        evento.stopPropagation();

        this.senha = evento.target.value;
    }

    _handleMudancaEstado(evento) {
        evento.stopPropagation();

        this.estado = evento.target.value;
    }

        render() {
            return (
            <>
                <TituloFormulario />
                <form classNameName="formulario" onSubmit={this._criarPessoa.bind(this)}>
                    <div className="row mb-3">
                        <label for="inputEmail3" className="col-sm-2 col-form-label">Email</label>
                        <div className="col-sm-10">
                            <input id='email' type="email" className="form-control" id="inputEmail3" 
                            onChange={this._handleMudancaEmail.bind(this)}/>
                        </div>
                    </div>
                    <div className="row mb-3">
                        <label for="inputPassword3" className="col-sm-2 col-form-label">Senha</label>
                        <div className="col-sm-10">
                            <input id='senha' type="password" className="form-control" id="inputPassword3" 
                            onChange={this._handleMudancaSenha.bind(this)}/>
                        </div>
                    </div>
                    <fieldset className="row mb-3">
                        <legend className="col-form-label col-sm-2 pt-0">Posição</legend>
                        <div className="col-sm-10">
                            <div className="form-check">
                                <input className="form-check-input" type="radio" name="gridRadios" id="gridRadios1" value="Professor" checked 
                                onChange={this._handleMudancaEstado.bind(this)}/>
                                <label className="form-check-label" for="gridRadios1">
                                    Professor
                                </label>
                            </div>
                            <div className="form-check">
                                <input className="form-check-input" type="radio" name="gridRadios" id="gridRadios2" value="Aluno" 
                                onChange={this._handleMudancaEstado.bind(this)}/>
                                <label className="form-check-label" for="gridRadios2">
                                    Aluno
                                </label>
                            </div>
                        </div>
                    </fieldset>

                    <button type="submit" className="btn btn-primary">Enviar</button>
                </form>
            </>

        )
    }
}


export default Formulario;
